//imports
    //react
        import React from 'react';
    //materials
        import Button from '@mui/material/Button';
        import DialogActions from '@mui/material/DialogActions';
        import DialogContent from '@mui/material/DialogContent';
        import DialogTitle from '@mui/material/DialogTitle';
        import TextField from '@mui/material/TextField';
        import Radio from '@mui/material/Radio';
        import RadioGroup from '@mui/material/RadioGroup';
        import FormControlLabel from '@mui/material/FormControlLabel';
        import FormControl from '@mui/material/FormControl';
        import FormLabel from '@mui/material/FormLabel';
        //components
        import DateTime from './dateTime';

//master export
    export default function ResponsiveDialog(props) {
    //variables
        let [deadline] = React.useState(props.dataFromParent.deadline);
        let [inputs, setInputs] = React.useState(props.dataInputs);
        let [rows, setRows] = React.useState(props.dataRows);
    //cancel
        let cancel = () => {
            props.parentCallback({
                action: 'cancel',
                data: {}
            });
        };

        let handleInputChange = (idx, event) => {
            const value = event.target.value;
            const name = event.target.placeholder;
            let error = '';
            if (!value.match(/^[a-zA-Z]+$/)) {
                error = `${name} is Required!`
            }
            inputs[idx] = {...inputs[idx], value: value, error: error}
            setInputs (prevInputs => {
                return {...prevInputs}
            })
        }

        let handleInputUnique = (event) => {
            let errorDup = '';
            // rows.map((row) => {
            //     if (row.title == inputs[0].value) {
            //         errorDup = `${inputs[0].name} is duplicated!`
            //         inputs[0] = {...inputs[0], errorDup: errorDup}
            //         setInputs (prevInputs => {
            //             return {...prevInputs}
            //         })
            //     }
            // });
            
            rows.push({title: inputs[0].value,
                        description: inputs[1].value,
                        deadline: deadline.format("MM/DD/YYYY")});
            setRows (prevRows => {
                return {...prevRows}
            })
            cancel()
        }

    //return master object
        return (
            <>       
            {/*title*/}
                <DialogTitle sx = {{ bgcolor: 'primary.dark', color: 'white'}}>
                <i className = "fa fa-fw fa-plus-circle"></i>Add Task
                </DialogTitle>
            {/*content*/}
                <DialogContent>
                    <br /><br />
                    {<TextField  placeholder={inputs[0].name} sx={{width: 1}} onChange={(e) => handleInputChange(0, e)}/>}
                    {inputs[0].error && <div style={{ color: "red", padding: "3px 0 0 12px"}}>{inputs[0].error}</div> }
                    <br /><br />
                    <TextField  placeholder={inputs[1].name} sx={{width: 1}} onChange={(e) => handleInputChange(1, e)}/>
                    {inputs[1].error && <div style={{ color: "red", padding: "3px 0 0 12px"}}>{inputs[1].error}</div> }
                {/*deadline*/}
                    <br /><br />
                    <DateTime dataFromParent = {deadline} dataToParent = {deadline}/>
                    <br /><br />
                    <FormLabel id="demo-row-radio-buttons-group-label">Priority</FormLabel>
                        <RadioGroup
                            row
                            aria-labelledby="demo-row-radio-buttons-group-label"
                            name="row-radio-buttons-group"
                        >
                            <FormControlLabel value="low" control={<Radio />} label="Low" />
                            <FormControlLabel value="med" control={<Radio />} label="Med" />
                            <FormControlLabel value="high" control={<Radio />} label="High" />
                        </RadioGroup>
                </DialogContent>
            {/*action buttons*/}
                <DialogActions sx={{ bgcolor: 'white'}}>
                        {<Button  variant = "contained"  sx = {{bgcolor: 'primary.dark', width: 100}} onClick={(e) => handleInputUnique(e)}>
                        <i className = "fa fa-fw fa-plus-circle"></i>Add
                        </Button>}
                    {/*cancel button*/}
                        <Button onClick = {cancel} variant = "contained" color = 'error' sx = {{bgcolor: '#f44336', width: 100}}>
                        <i className = "fa fa-fw fa-ban"></i>&nbsp;Cancel
                        </Button>
                </DialogActions>
            </>
        );
    }
